mapper.update:
UPDATE account
SET admin = #{admin}
WHERE account_number = #{accountNumber}

mapper.select:
SELECT account_number, id, name, admin
FROM account
WHERE active = 1 AND id = #{id} AND password = #{password}

SELECT id, name, email, phone, postcode, address, address_detail
FROM account
WHERE account_number = #{accountNumber}

mapper.update:
UPDATE account
SET active = 0
WHERE account_number = #{accountNumber} AND password = #{password}
